pragma solidity ^0.6.4;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract MockRenBTC is ERC20 {
    constructor() ERC20("Mock RenBTC", "renBTC") public {
        _setupDecimals(8);
        _mint(_msgSender(), 2100000000000000);
    }
}

contract MockWBTC is ERC20 {
    constructor() ERC20("Mock WBTC", "wBTC") public {
        _setupDecimals(8);
        _mint(_msgSender(), 2100000000000000);
    }
}
