pragma solidity ^0.6.4;

import "./MultiCollateralBitcoin.sol";

interface IGateway {
    function mint(bytes32 _pHash, uint256 _amount, bytes32 _nHash, bytes calldata _sig) external returns (uint256);
    function burn(bytes calldata _to, uint256 _amount) external returns (uint256);
}

interface IGatewayRegistry {
    function getGatewayBySymbol(string calldata _tokenSymbol) external view returns (IGateway);
    function getTokenBySymbol(string calldata _tokenSymbol) external view returns (IERC20);
}

contract RenBTCAdapter {
    IGatewayRegistry public registry;
    MultiCollateralBitcoin public mcBTC;

    constructor(IGatewayRegistry _registry, MultiCollateralBitcoin _mcBTC) public {
        registry = _registry;
        mcBTC = _mcBTC;
    }

    function deposit(
        // Parameters from users
        bytes calldata _msg,
        // Parameters from Darknodes
        uint256        _amount,
        bytes32        _nHash,
        bytes calldata _sig
    ) external {
        bytes32 pHash = keccak256(abi.encode(_msg));
        uint256 mintedAmount = registry.getGatewayBySymbol("BTC").mint(pHash, _amount, _nHash, _sig);
        IERC20 renBTC = registry.getTokenBySymbol("BTC");
        renBTC.approve(address(mcBTC), mintedAmount);
        mcBTC.deposit(address(renBTC), mintedAmount);
        mcBTC.transfer(msg.sender, mintedAmount);
    }

    function withdraw(bytes calldata _to, uint256 _amount) external {
        IERC20 renBTC = registry.getTokenBySymbol("BTC");
        mcBTC.transferFrom(msg.sender, address(this), _amount);
        uint256 withdrewAmount = mcBTC.withdraw(address(renBTC), _amount);
        registry.getGatewayBySymbol("BTC").burn(_to, withdrewAmount);
    }

    function swapOut(address _fromBtcVariant, bytes calldata _to, uint256 _amount) external {
        IERC20 renBTC = registry.getTokenBySymbol("BTC");
        IERC20(_fromBtcVariant).transferFrom(msg.sender, address(this), _amount);
        IERC20(_fromBtcVariant).approve(address(mcBTC), _amount);
        uint256 withdrewAmount = mcBTC.swap(_fromBtcVariant, address(renBTC), _amount);
        registry.getGatewayBySymbol("BTC").burn(_to, withdrewAmount);
    }

    function swapIn(
        // Parameters from users
        bytes calldata _msg,
        // Parameters from Darknodes
        uint256        _amount,
        bytes32        _nHash,
        bytes calldata _sig
    ) external {
        address btcVariant;
        (btcVariant) = abi.decode(_msg, (address));
        bytes32 pHash = keccak256(abi.encode(_msg));
        uint256 mintedAmount = registry.getGatewayBySymbol("BTC").mint(pHash, _amount, _nHash, _sig);
        IERC20 renBTC = registry.getTokenBySymbol("BTC");
        renBTC.approve(address(mcBTC), mintedAmount);
        uint256 withdrewAmount = mcBTC.swap(address(renBTC), btcVariant, mintedAmount);
        IERC20(btcVariant).transfer(msg.sender, withdrewAmount);
    }
}
